// –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—Å—Ç - —Å –∑–∞—â–∏—Ç–æ–π –æ—Ç undefined
let text = $json.output || $json.text || $json.message || '';

// –ï—Å–ª–∏ output –ø—É—Å—Ç–æ–π, –ø—Ä–æ–±—É–µ–º –≤–∑—è—Ç—å video_url –Ω–∞–ø—Ä—è–º—É—é
if (!text && $json.video_url) {
  text = '–í–∏–¥–µ–æ –≥–æ—Ç–æ–≤–æ! URL: ' + $json.video_url;
}

// –ï—Å–ª–∏ –≤—Å—ë –µ—â—ë –ø—É—Å—Ç–æ
if (!text) {
  text = '–†–µ–∑—É–ª—å—Ç–∞—Ç –ø–æ–ª—É—á–µ–Ω, –Ω–æ —Ç–µ–∫—Å—Ç –ø—É—Å—Ç–æ–π';
}

// –§—É–Ω–∫—Ü–∏—è —ç–∫—Ä–∞–Ω–∏—Ä–æ–≤–∞–Ω–∏—è Markdown-—Å–ø–µ—Ü—Å–∏–º–≤–æ–ª–æ–≤
function escapeMarkdownV2(text) {
  if (!text || typeof text !== 'string') return '';
  return text
    .replace(/_/g, '\\_')
    .replace(/\*/g, '\\*')
    .replace(/\[/g, '\\[')
    .replace(/\]/g, '\\]')
    .replace(/\(/g, '\\(')
    .replace(/\)/g, '\\)')
    .replace(/~/g, '\\~')
    .replace(/`/g, '\\`')
    .replace(/>/g, '\\>')
    .replace(/#/g, '\\#')
    .replace(/\+/g, '\\+')
    .replace(/-/g, '\\-')
    .replace(/=/g, '\\=')
    .replace(/\|/g, '\\|')
    .replace(/\{/g, '\\{')
    .replace(/\}/g, '\\}')
    .replace(/\./g, '\\.')
    .replace(/!/g, '\\!');
}

return [{
  json: {
    escapedText: escapeMarkdownV2(text),
    originalText: text
  }
}];

// –ü–æ–ª—É—á–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –Ω–∞–ø—Ä—è–º—É—é –∏–∑ —Ç—Ä–∏–≥–≥–µ—Ä–∞
const triggerData = $('–¢—Ä–∏–≥–≥–µ—Ä Telegram (–≤—Ö–æ–¥—è—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ)').item.json;
const message = triggerData.message;

const presetResult = $input.first().json;

// –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ –º–∞—Å—Å–∏–≤ –µ—Å–ª–∏ –Ω—É–∂–Ω–æ
const presetData = Array.isArray(presetResult) ? presetResult : [presetResult];

// –ï—Å–ª–∏ preset –Ω–∞–π–¥–µ–Ω
if (presetData.length > 0 && presetData[0].timestamp) {
  // –°–æ—Ä—Ç–∏—Ä—É–µ–º –ø–æ timestamp (–Ω–æ–≤—ã–µ –ø–µ—Ä–≤—ã–º–∏)
  presetData.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));

  const preset = presetData[0];
  const timestamp = new Date(preset.timestamp);
  const now = new Date();
  const minutesAgo = (now - timestamp) / 1000 / 60;

  if (minutesAgo < 5) {
    // Preset –∞–∫—Ç—É–∞–ª–µ–Ω
    const filterParams = typeof preset.filter_params === 'string' 
      ? JSON.parse(preset.filter_params) 
      : preset.filter_params;

    return [{
      json: {
        message: message,
        preset_active: true,
        preset_name: preset.preset_name,
        preset_params: filterParams
      }
    }];
  }
}

// Preset –Ω–µ –Ω–∞–π–¥–µ–Ω –∏–ª–∏ —É—Å—Ç–∞—Ä–µ–ª
return [{
  json: {
    message: message,
    preset_active: false
  }
}];

// –§–æ—Ä–º–∏—Ä—É–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
const error = $json;
const errorMsg = error.execution?.error?.message || error.message || 'Unknown error';
const nodeName = error.execution?.lastNodeExecuted || 'Unknown node';

// –ü–æ–ª—É—á–∞–µ–º chat_id –∏–∑ –¥–∞–Ω–Ω—ã—Ö –≤–æ—Ä–∫—Ñ–ª–æ—É
let chatId = null;
try {
  const workflowData = error.execution?.data?.resultData?.runData;
  if (workflowData) {
    const triggerData = workflowData['Telegram Trigger'] || workflowData['–¢—Ä–∏–≥–≥–µ—Ä Telegram (–≤—Ö–æ–¥—è—â–µ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ)'];
    if (triggerData && triggerData[0]) {
      chatId = triggerData[0].data?.main?.[0]?.[0]?.json?.message?.chat?.id;
    }
  }
} catch(e) {}

return [{
  json: {
    chatId: chatId,
    errorText: 'Error in ' + nodeName + ': ' + errorMsg
  }
}];
