      vfr: vfr.toFixed(4),
      er: er.toFixed(2),
      virality: virality,
      engagement: engagement,
      type: 'video',
      added_date: new Date().toISOString()
    }
  };
});

// –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –Ω–æ–≤—ã–µ –≤–∏–¥–µ–æ –∏ —à–æ—Ä—Ç—Å—ã –∏–∑ batch –ø–∞—Ä—Å–∏–Ω–≥–∞
const items = $input.all();
const results = [];

for (const item of items) {
  const videos = item.json.videos || [];
  const shorts = item.json.shorts || [];
  const channelData = item.json;
  const followers = channelData.subscriberCount || 0;

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤–∏–¥–µ–æ
  for (const v of videos) {
    const views = v.viewCount || 0;
    const likes = v.likes || 0;
    const comments = v.commentsCount || 0;

    const vfr = followers > 0 ? (views / followers) : 0;
    const er = views > 0 ? ((likes + comments) / views * 100) : 0;

    let virality = 'LOW';
    if (vfr > 1) virality = 'VIRUS';
    else if (vfr > 0.3 || views > 100000) virality = 'AVERAGE';

    let engagement = 'LOW ER';
    if (er > 5) engagement = 'BEST ER';
    else if (er > 2) engagement = 'AVERAGE ER';

    results.push({
      json: {
        media_id: v.id,
        media_url: v.url,
        cover_url: v.thumbnailUrl,
        title: v.title || '',
        views, likes, comments,
        duration: v.duration || 0,
        posted_date: v.date || '',
        channel_username: channelData.channelUsername || '',
        channel_subscribers: followers,
        vfr: vfr.toFixed(4),
        er: er.toFixed(2),
        virality, engagement,
        type: 'video',
        added_date: new Date().toISOString()
      }
    });
  }

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ shorts
  for (const s of shorts) {
    const views = s.viewCount || 0;
    const likes = s.likes || 0;
    const comments = s.commentsCount || 0;

    const vfr = followers > 0 ? (views / followers) : 0;
    const er = views > 0 ? ((likes + comments) / views * 100) : 0;

    let virality = 'LOW';
    if (vfr > 1) virality = 'VIRUS';
    else if (vfr > 0.3 || views > 100000) virality = 'AVERAGE';

    let engagement = 'LOW ER';
    if (er > 5) engagement = 'BEST ER';
    else if (er > 2) engagement = 'AVERAGE ER';

    results.push({
      json: {
        media_id: s.id,
        media_url: s.url,
        cover_url: s.thumbnailUrl,
        title: s.title || '',
        views, likes, comments,
        duration: s.duration || 0,
        posted_date: s.date || '',
        channel_username: channelData.channelUsername || '',
        channel_subscribers: followers,
        vfr: vfr.toFixed(4),
        er: er.toFixed(2),
        virality, engagement,
        type: 'shorts',
        added_date: new Date().toISOString()
      }
    });
  }
}

return results;

// –ò–∑–≤–ª–µ–∫–∞–µ–º —Ç—Ä–∞–Ω—Å–∫—Ä–∏–ø—Ç
